package JavaBasics;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		//Primitive data types: int, double, float, boolean
		//1. integers
		
		int i=10;
		int j=20;
		System.out.println(j);
		System.out.println(i+j);
		int k=32;
		int l=40;
		
		// 2. double

		
		double m=11.11;
		double d1=21.22;
		double d2= 23;
		double d3=-100;
		
		
		System.out.println(d1 + " " +d2);
		
		
		//3. Char: character single digit value, must be in single quotes.  
		
		char c= 'a';
		char c1= 'A';
		char c2='1';
		
		
		//4. boolean
		
		boolean b= true;
		boolean b1= false;
		
		//5. String : is a class not a data type.
		
		String S="Amiot ";
		String S1= "1000";
		String S2= "Animation";
		
	}

}
