package ArraysDemo;

public class stringsssss {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		for(int i=0;i<10;i++)
		{
			
			int k=3;
			for(int j=0;j<0+i;j++)
			{
				
				System.out.print(k+ " ");
				k=k*i;
				
			}
			System.out.println("");
			
		}
	}
}
